INSERT INTO `t_operation_log` VALUES ('1711', '文章模块', '新增或修改', '/admin/articles', 'com.aurora.controller.ArticleController.saveOrUpdateArticle', '保存和修改文章', '[{\"articleAbstract\":\"Nginx防恶意频繁访问网站\",\"articleContent\":\"在nginx中设置防恶意频繁访问网站，你可以使用limit_req模块来实现。下面是一个示例的配置：\\n\\n```nginx\\nhttp {\\n    limit_req_zone $binary_remote_addr zone=one:10m rate=1r/s;\\n\\n    server {\\n        ...\\n\\n        location / {\\n            limit_req zone=one burst=5 nodelay;\\n            ...\\n        }\\n    }\\n}\\n```\\n\\n在上述配置中，使用`limit_req_zone`来定义一个名为`one`的限制请求的zone。这里使用了`$binary_remote_addr`变量作为限制请求的键，以区分不同的客户端。`10m`指定了zone的大小，可以存储10兆字节的请求信息。`rate=1r/s`表示每秒只允许一个请求。\\n\\n接下来，在需要限制请求的`location`块中使用`limit_req`指令来应用限制。`burst=5`表示当超过限制请求速率时，可以允许的突发请求数量为5个。`nodelay`表示不延迟处理请求，而是立即返回503错误响应。\\n\\n用以上配置可以限制对网站的恶意频繁访问。你可以根据实际需求调整限制请求的速率和突发请求数量。\",\"articleCover\":\"https://blog-aurora-weh.oss-cn-shenzhen.aliyuncs.com/aurora/articles/0f2adc2ea072b74acd2aa2a00d481f41.webp\",\"articleTitle\":\"Nginx防恶意频繁访问网站\",\"categoryName\":\"test\",\"id\":135,\"isFeatured\":0,\"isTop\":1,\"status\":1,\"tagNames\":[],\"type\":1}]', 'POST', '{\"code\":20000,\"flag\":true,\"message\":\"操作成功\"}', '1', '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-07-20 21:17:22', null);
INSERT INTO `t_operation_log` VALUES ('1712', '文章模块', '新增或修改', '/admin/articles', 'com.aurora.controller.ArticleController.saveOrUpdateArticle', '保存和修改文章', '[{\"articleAbstract\":\"Nginx防恶意频繁访问网站\",\"articleContent\":\"在nginx中设置防恶意频繁访问网站，你可以使用limit_req模块来实现。下面是一个示例的配置：\\n\\n```nginx\\nhttp {\\n    limit_req_zone $binary_remote_addr zone=one:10m rate=1r/s;\\n\\n    server {\\n        ...\\n\\n        location / {\\n            limit_req zone=one burst=5 nodelay;\\n            ...\\n        }\\n    }\\n}\\n```\\n\\n在上述配置中，使用`limit_req_zone`来定义一个名为`one`的限制请求的zone。这里使用了`$binary_remote_addr`变量作为限制请求的键，以区分不同的客户端。`10m`指定了zone的大小，可以存储10兆字节的请求信息。`rate=1r/s`表示每秒只允许一个请求。\\n\\n接下来，在需要限制请求的`location`块中使用`limit_req`指令来应用限制。`burst=5`表示当超过限制请求速率时，可以允许的突发请求数量为5个。`nodelay`表示不延迟处理请求，而是立即返回503错误响应。\\n\\n用以上配置可以限制对网站的恶意频繁访问。你可以根据实际需求调整限制请求的速率和突发请求数量。\",\"articleCover\":\"https://blog-aurora-weh.oss-cn-shenzhen.aliyuncs.com/aurora/articles/0f2adc2ea072b74acd2aa2a00d481f41.webp\",\"articleTitle\":\"Nginx防恶意频繁访问网站\",\"categoryName\":\"test\",\"id\":135,\"isFeatured\":0,\"isTop\":1,\"status\":1,\"tagNames\":[],\"type\":1}]', 'POST', '{\"code\":20000,\"flag\":true,\"message\":\"操作成功\"}', '1', '演示账号', '127.0.0.1', '内网IP|内网IP', '2024-07-20 21:19:09', null);
